mapper.select:
SELECT * FROM pboard
where member_no=#{member_no}
and (title like '%${sword}%') 
ORDER BY writedate DESC
LIMIT  ${startIdx},${pageRow}

SELECT COUNT(*) FROM pboard 
where member_no=#{member_no}
and (title like '%${sword}%')

select m.nickname, m.id, me.message_content, me.senddate from pmessage me join pmember m on m.member_no = me.send_member_no where receive_member_no=#{receive_member_no}
and (${stype} LIKE '%${sword}%')
and (m.nickname LIKE '%${sword}%' OR me.message_content LIKE '%${sword}%')
ORDER BY me.senddate DESC LIMIT ${startIdx},${pageRow};

SELECT COUNT(*) from pmessage me join pmember m on m.member_no = me.send_member_no where receive_member_no=#{receive_member_no}
and (${stype} LIKE '%${sword}%')
and (m.nickname LIKE '%${sword}%' OR me.message_content LIKE '%${sword}%')

