mapper.insert:
INSERT INTO preply (
	content, member_no, board_no, reply_writedate , reply_no, board_name
) VALUES (
	#{content}, #{member_no}, #{board_no},NOW(),#{reply_no},#{board_name}
)

mapper.select:
SELECT *

FROM preply
WHERE board_no = #{board_no} AND board_name = #{board_name}
ORDER BY reply_writedate DESC
LIMIT  ${startIdx},${pageRow};
