mapper.insert:
INSERT INTO pmember (
	id, nickname, pwd, email, m_filename_org, m_filename_server, regdate
) VALUES (
	#{id}, #{nickname}, md5(#{pwd}), #{email}, #{m_filename_org}, #{m_filename_server}, NOW()
)

mapper.update:
update pmember set
	nickname = #{nickname}
	, pwd = md5(#{pwd})
	, email = #{email}
	
	, m_filename_org = #{m_filename_org}
	, m_filename_server = #{m_filename_server}
	
where id = #{id}
